import React, {useEffect, useState} from 'react'
import axios from 'axios'
import { useNavigate, useParams } from 'react-router-dom'
import BackButton from '../components/BackButton'
import Spinner from '../components/Spinner'
import { useSnackbar } from 'notistack'


function EditBook() {
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [publishYear, setPublishYear] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const {enqueueSnackbar} = useSnackbar();
  const {id} = useParams();

  useEffect(()=> {
    setLoading(true);
    axios
      .get(`https://bookstore-mern-stack-yvz9.onrender.com/books/${id}`)
      .then((response) => {
        setAuthor(response.data.author);
        setPublishYear(response.data.publishYear);
        setTitle(response.data.title);
        setLoading(false);

      })
      .catch((error) => {
        setLoading(false);
        alert('An error occured. Please check console');
        console.log(error);
      });
  },[])


  const handelEditBook = () => {
    const data = {title, author, publishYear};
    setLoading(true);
    axios
      .put(`https://bookstore-mern-stack-yvz9.onrender.com/books/${id}`,data)
      .then(() => {
        setLoading(false);
        enqueueSnackbar('Book updated sucessfully', {variant: 'success'});
        navigate('/');
      })
      .catch((error) => {
        setLoading(false);
        enqueueSnackbar('An error occured', {variant: 'error'});
      });
  }

  return (
    <div className='p-4'>
      <BackButton />
      <h1 className='text-3xl my-4'> Edit Book </h1>
      {loading ? <Spinner /> : ""}
      <div className='flex flex-col border-2 border-sky-400 rounded-xl w-[600px] p-4 mx-auto'>
        <div className='my-4'>
          <label className='text-xl mr-4 text-gray-500'>Title</label>
          <input 
            type='text' 
            value={title} 
            onChange={(e) => setTitle(e.target.value)} 
            className='border-2 border-gray-500 px-4 py-2 w-full'
          />
        </div>
        <div className='my-4'>
          <label className='text-xl mr-4 text-gray-500'>Author</label>
          <input 
            type='text' 
            value={author} 
            onChange={(e) => setAuthor(e.target.value)} 
            className='border-2 border-gray-500 px-4 py-2 w-full'
          />
        </div>
        <div className='my-4'>
          <label className='text-xl mr-4 text-gray-500'>Publish Year</label>
          <input 
            type='number' 
            value={publishYear} 
            onChange={(e) => setPublishYear(e.target.value)} 
            className='border-2 border-gray-500 px-4 py-2 w-full'
          />
        </div>
        <button className='p-2 bg-sky-300 m-8' onClick={handelEditBook} > Save </button>
      </div>

    </div>
  )
}

export default EditBook
