import React, {useEffect, useState} from 'react'
import axios from 'axios'
import { useNavigate, useParams } from 'react-router-dom'
import BackButton from '../components/BackButton'
import Spinner from '../components/Spinner'
import { useSnackbar } from 'notistack'

function DeleteBook() {
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();
  const {enqueueSnackbar} = useSnackbar();
  const {id} = useParams();

  const handelDeleteBook = () => {
    setLoading(true);
    axios
      .delete(`https://bookstore-mern-stack-yvz9.onrender.com/books/${id}`)
      .then(() => {
        setLoading(false);
        enqueueSnackbar('Book deleted sucessfully', {variant: 'success'});
        navigate('/');
      })
      .catch((error) => {
        setLoading(false);
        enqueueSnackbar('An error occured', {variant: 'error'});

      });

  };

  return (
    <div className='p-4'>
      <BackButton />
      <h1 className='text-3xl my-4'>Delete Book</h1>
      {loading ? <Spinner /> : ''}
      <div className='flex flex-col items-center border-2 border-sky-400 rounded-xl w-[600px] p-8 mx-auto' >
        <h3 className='text-2xl'> Are you sure you want to delete this book record?</h3>
        <button 
        className='p-4 bg-red-600 text-white m-8 w-full'
        onClick={handelDeleteBook}> Yes, Delete it</button>
      </div>

    </div>
  )
}

export default DeleteBook
